			{
				"value": 4.00000000,
				"n": 0,
				"scriptPubKey": {
					"asm": "OP_DUP OP_HASH160 61281b74375a87a0ed0a63c4b8540fc8cffbbf19 OP_EQUALVERIFY OP_CHECKSIG",
