make HOST=i686-pc-linux-gnu
cd ..
./autogen.sh # not required when building from tarball
CONFIG_SITE=$PWD/depends/i686-pc-linux-gnu/share/config.site ./configure --prefix=/$(pwd)/depends/i686-pc-linux-gnu
make

