+// Copyright (c) 2016 The Bitcoin Core developers
+// Distributed under the MIT software license, see the accompanying
+// file COPYING or http://www.opensource.org/licenses/mit-license.php.
+
+#include "blockencodings.h"
+#include "consensus/consensus.h"
+#include "hash.h"
+#include "random.h"
+#include "streams.h"
+#include "txmempool.h"
+#include "util.h"
+
+
+#define MIN_TRANSACTION_SIZE 60
+
+CBlockHeaderAndShortTxIDs::CBlockHeaderAndShortTxIDs(const CBlock& block) :
+        nonce(GetRand(std::numeric_limits<uint64_t>::max())),
+        shorttxids(block.vtx.size() - 1), prefilledtxn(1), header(block) {
+    FillShortTxIDSelector();
+    //TODO: Use our mempool prior to block acceptance to predictively fill more than just the coinbase
+    prefilledtxn[0] = {0, block.vtx[0]};
+    for (size_t i = 1; i < block.vtx.size(); i++) {
+        const CTransaction& tx = block.vtx[i];
+        shorttxids[i - 1] = GetShortID(tx.GetHash());
+    }
+}
+
+void CBlockHeaderAndShortTxIDs::FillShortTxIDSelector() const {
+    CDataStream stream(SER_NETWORK, PROTOCOL_VERSION);
+    stream << header;
+    CSHA256 hasher;
+    hasher.Write((unsigned char*)&(*stream.begin()), stream.end() - stream.begin());
+    hasher.Write((unsigned char*)&nonce, sizeof(nonce));
+    uint256 shorttxidhash;
+    hasher.Finalize(shorttxidhash.begin());
+    shorttxidk0 = shorttxidhash.GetUint64(0);
+    shorttxidk1 = shorttxidhash.GetUint64(1);
+}
+
+uint64_t CBlockHeaderAndShortTxIDs::GetShortID(const uint256& txhash) const {
+    static_assert(SHORTTXIDS_LENGTH == 6, "shorttxids calculation assumes 6-byte shorttxids");
+    return SipHashUint256(shorttxidk0, shorttxidk1, txhash) & 0xffffffffffffL;
+}
+
+
+
+ReadStatus PartiallyDownloadedBlock::InitData(const CBlockHeaderAndShortTxIDs& cmpctblock) {
+    if (cmpctblock.header.IsNull() || (cmpctblock.shorttxids.empty() && cmpctblock.prefilledtxn.empty()))
+        return READ_STATUS_INVALID;
+    if (cmpctblock.shorttxids.size() + cmpctblock.prefilledtxn.size() > MAX_BLOCK_SIZE / MIN_TRANSACTION_SIZE)
+        return READ_STATUS_INVALID;
+
+    assert(header.IsNull() && txhashes.empty());
+    header = cmpctblock.header;
+    txhashes.resize(cmpctblock.shorttxids.size() + cmpctblock.prefilledtxn.size());
+
+    prefilledtxn.reserve(cmpctblock.prefilledtxn.size());
+    int32_t lastprefilledindex = -1;
+    for (size_t i = 0; i < cmpctblock.prefilledtxn.size(); i++) {
+        if (cmpctblock.prefilledtxn[i].tx.IsNull())
+            return READ_STATUS_INVALID;
+
+        lastprefilledindex += cmpctblock.prefilledtxn[i].index + 1;
+        if (lastprefilledindex > std::numeric_limits<uint16_t>::max())
+            return READ_STATUS_INVALID;
+        if ((uint32_t)lastprefilledindex > cmpctblock.shorttxids.size() + i + 1) {
+            // If we are inserting a tx at an index greater than our full list of shorttxids
+            // plus the number of prefilled txn we've inserted, then we have txn for which we
+            // have neither a prefilled txn or a shorttxid!
+            return READ_STATUS_INVALID;
+        }
+        txhashes[lastprefilledindex] = cmpctblock.prefilledtxn[i].tx.GetHash();
+        prefilledtxn.push_back({(uint16_t)lastprefilledindex, cmpctblock.prefilledtxn[i].tx});
+    }
+
+    // Calculate map of txids -> positions and check mempool to see what we have (or dont)
+    std::map<uint64_t, uint16_t> shorttxids;
+    uint16_t index_offset = 0;
+    std::vector<PrefilledTransaction>::const_iterator prefilledit = prefilledtxn.begin();
+    for (size_t i = 0; i < cmpctblock.shorttxids.size(); i++) {
+        while (prefilledit != prefilledtxn.end() && i + index_offset == prefilledit->index) {
+            index_offset++;
+            prefilledit++;
+        }
+        shorttxids[cmpctblock.shorttxids[i]] = i + index_offset;
+    }
+    if (shorttxids.size() != cmpctblock.shorttxids.size())
+        return READ_STATUS_FAILED; // Short ID collision
+
+    char have_txn[txhashes.size() / 8 + 1];
+    memset(have_txn, 0, txhashes.size() / 8 + 1);
+
+    LOCK(pool->cs);
+    for (CTxMemPool::txiter it = pool->mapTx.begin(); it != pool->mapTx.end(); it++) {
+        std::map<uint64_t, uint16_t>::iterator idit = shorttxids.find(cmpctblock.GetShortID(it->GetTx().GetHash()));
+        if (idit != shorttxids.end()) {
+            if (!(have_txn[idit->second / 8] & (1 << (idit->second & 0x7)))) {
+                it->AddRef();
+                txhashes[idit->second] = it->GetTx().GetHash();
+                have_txn[idit->second / 8] |= 1 << (idit->second & 0x7);
+            } else {
+                // If we find two mempool txn that match the short id, just request it.
+                // This should be rare enough that the extra bandwidth doesn't matter,
+                // but eating a round-trip due to FillBlock failure would be annoying
+                pool->ReleaseTxLock(txhashes[idit->second]);
+                txhashes[idit->second].SetNull();
+            }
+        }
+    }
+
+    LogPrint("cmpctblock", "Initialized PartiallyDownloadedBlock for block %s using a cmpctblock of size %lu\n", cmpctblock.header.GetHash().ToString(), cmpctblock.GetSerializeSize(SER_NETWORK, PROTOCOL_VERSION));
+
+    return READ_STATUS_OK;
+}
+
+bool PartiallyDownloadedBlock::IsTxAvailable(size_t index) const {
+    assert(!header.IsNull());
+    assert(index < txhashes.size());
+    return !txhashes[index].IsNull();
+}
+
+ReadStatus PartiallyDownloadedBlock::FillBlock(CBlock& block, const std::vector<CTransaction>& vtx_missing) const {
+    assert(!header.IsNull());
+    block = header;
+    block.vtx.resize(txhashes.size());
+
+    size_t tx_missing_offset = 0;
+    std::vector<PrefilledTransaction>::const_iterator prefilledit = prefilledtxn.begin();
+    size_t prefilled_count = 0, missing_count = 0, mempool_count = 0;
+    for (size_t i = 0; i < txhashes.size(); i++) {
+        if (txhashes[i].IsNull()) {
+            if (vtx_missing.size() <= tx_missing_offset)
+                return READ_STATUS_INVALID;
+            block.vtx[i] = vtx_missing[tx_missing_offset++];
+            missing_count++;
+        } else {
+            if (prefilledit != prefilledtxn.end() && prefilledit->index == i) {
+                block.vtx[i] = prefilledit->tx;
+                prefilledit++;
+                prefilled_count++;
+            } else {
+                assert(pool->lookup(txhashes[i], block.vtx[i], true));
+                mempool_count++;
+            }
+        }
+    }
+    assert(prefilledit == prefilledtxn.end());
+    if (vtx_missing.size() == tx_missing_offset) {
+        LogPrint("cmpctblock", "Successfully reconstructed block %s with %lu txn prefilled, %lu txn from mempool and %lu txn requested\n", header.GetHash().ToString(), prefilled_count, mempool_count, missing_count);
+        if (vtx_missing.size() < 5) {
+            BOOST_FOREACH(const CTransaction& tx, vtx_missing)
+                LogPrint("cmpctblock", "Reconstructed block %s required tx %s\n", header.GetHash().ToString(), tx.GetHash().ToString());
+        }
+    }
+    return vtx_missing.size() == tx_missing_offset ? READ_STATUS_OK : READ_STATUS_INVALID;
+}
